INSERT INTO book (name, author, price, total_count, image_url, description) VALUES ('Airborne', 'Diann Mills', 10, 100, 'resources/img/airborne.jpg', 'Heather Lawrence''s long-awaited vacation to Salzburg wasn''t supposed to go like this. Mere hours into the transatlantic flight, the Houston FBI agent is awakened when passengers begin exhibiting horrific symptoms of an unknown infection');
INSERT INTO book (name, author, price, total_count, image_url, description) VALUES ('The Stand', 'Stephen King', 15, 70, 'resources/img/the_stand.jpg', 'When a man escapes from a biological testing facility, he sets in motion a deadly domino effect, spreading a mutated strain of the flu that will wipe out 99 percent of humanity within a few weeks.');
INSERT INTO book (name, author, price, total_count, image_url, description) VALUES ('The Mars Strain', 'A.C. Anderson', 12, 100, 'resources/img/the_mars_strain.jpg', 'We’ve colonized Mars, but we never should’ve come back. When the first astronauts of the Mars Colony returned to Earth, they brought a mysterious, metal box they had found half-buried in the red dirt, called the Mars Cube.');
INSERT INTO book (name, author, price, total_count, image_url, description) VALUES ('Infected', 'Scott Sigler', 9.99, 80, 'resources/img/infected.jpg', 'Across America a mysterious disease is turning ordinary people into raving, paranoid murderers who inflict brutal horrors on strangers, themselves, and even their own families.');
INSERT INTO book (name, author, price, total_count, image_url, description) VALUES ('Dead Love', 'Linda Wells', 12.99, 100, 'resources/img/dead_love.jpg', 'When a terrorist attack is unleashed on a major US metropolitan area, the lives of a beautiful scientist, her military lover, and two FBI agents become intertwined.');